import React, { useState } from "react";
import Button from "../UI/Button";
import { Link } from "react-router-dom";
import Dropdown from "../Dropdown/Dropdown";
import "./Navbar.css";

const Navbar = () => {
  const [showMenu, SetShowMenu] = useState(false);
  const [dropdown, setDropdown] = useState(false);

  const hamburgerHandler = () => SetShowMenu(!showMenu);
  const closeMobileView = () => SetShowMenu(false);
  return (
    <nav className="navbar">
      <Link href="/" className="navbar-logo">
        WebDev
      </Link>
      <div className="hamburger-menu">
        <i
          className={showMenu ? "fas fa-times" : "fas fa-bars"}
          onClick={hamburgerHandler}
        ></i>
      </div>

      <ul className={showMenu ? "nav-menu active" : "nav-menu"}>
        <li className="nav-item">
          <Link href="/" className="nav-links" onClick={closeMobileView}>
            Home
          </Link>
        </li>
        <li className="nav-item">
          <Link href="/" className="nav-links" onClick={closeMobileView}>
            Services <i className="fas fa-caret-down"></i>
          </Link>

          {dropdown && <Dropdown />}
        </li>
        <li className="nav-item">
          <Link href="/" className="nav-links" onClick={closeMobileView}>
            About Us
          </Link>
        </li>
        <li className="nav-item">
          <Link href="/" className="nav-links" onClick={closeMobileView}>
            Contact Us
          </Link>
        </li>

        <li className="nav-item">
          <Link href="/" className="nav-links-mobile" onClick={closeMobileView}>
            Sign Up
          </Link>
        </li>
      </ul>

      <Button />
    </nav>
  );
};

export default Navbar;
