import React, { useState } from "react";
import Button from "../UI/Button";
import Dropdown from "../Dropdown/Dropdown";
import { Link } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
  const [showMenu, setShowMenu] = useState(false);
  const [dropdown, setDropdown] = useState(false);

  const hamburgerHandler = () => setShowMenu(!showMenu);
  const closeMobileView = () => setShowMenu(false);

  const mouseEnterHandler = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(true);
    }
  };

  const mouseLeaveHandler = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(false);
    }
  };
  return (
    <nav className="navbar">
      <Link to="/" className="navbar-logo">
        MNM
      </Link>

      <div className="hamburger-menu">
        <i
          className={showMenu ? "fas fa-times" : "fas fa-bars"}
          onClick={hamburgerHandler}
        />
      </div>

      <ul className={showMenu ? "nav-menu active" : "nav-menu "}>
        <li className="menu-item">
          <Link to="/" className="nav-links" onClick={closeMobileView}>
            Home
          </Link>
        </li>
        <li
          className="menu-item"
          onMouseEnter={mouseEnterHandler}
          onMouseLeave={mouseLeaveHandler}
        >
          <Link to="/" className="nav-links" onClick={closeMobileView}>
            Services <i className="fas fa-caret-down" />
          </Link>
          {dropdown && <Dropdown />}
        </li>
        <li className="menu-item">
          <Link to="/" className="nav-links" onClick={closeMobileView}>
            About Us
          </Link>
        </li>
        <li className="menu-item">
          <Link to="/" className="nav-links" onClick={closeMobileView}>
            Contact Us
          </Link>
        </li>
        <li className="menu-item">
          <Link to="/" className="nav-links-mobile" onClick={closeMobileView}>
            Sign Up
          </Link>
        </li>
      </ul>
      <Button />
    </nav>
  );
};

export default Navbar;
