import React, {useState} from "react";
import Button from "../UI/Button";
import Dropdown from "../Dropdown/Dropdown";
import "./Navbar.css";

const Navbar = () => {
    const [showMenu, SetShowMenu] = useState(false);

    const hamburgerHandler = () => SetShowMenu(!showMenu);
    return (
        <nav className="navbar">
            <a href="/" className='navbar-logo'>WebDev</a>
            <div className="hamburger-menu">
                <i className={showMenu ? 'fas fa-times' : 'fas fa-bars'} onClick={hamburgerHandler}></i>
            </div>

            <ul className={showMenu ? 'nav-menu active' : 'nav-menu'}></ul>
        </nav>
    );
};

export default Navbar;
