import React, { useState } from "react";
import GoalInput from "../goalInput/GoalInput";
import GoalItemList from "../GoalItemList/GoalItemList";
import "./App.css";

const App = () => {
  const [enteredGoalValue, setEnteredGoalValue] = useState([
    {
      text: "Enter a new goal",
      id: "g1",
    },
    { text: "Fun with this React project", id: "g2" },
  ]);

  const addItemHandler = (enteredText) => {
    setEnteredGoalValue((prevGoals) => {
      const updateGoals = [...prevGoals];
      updateGoals.unshift({
        text: enteredText,
        id: Math.floor(Math.random()).toString(),
      });
      return updateGoals;
    });
  };

  let contentText = (
    <p style={{ textAlign: "center" }}>Founds NO goal, Maybe add one!</p>
  );

  if(enteredGoalValue.length > 0) {
      <GoalItemList items={enteredGoalValue}/>
  }
  return (
    <div>
      <section id="forms">
        <GoalInput onAddGoal={addItemHandler}/>
      </section>

      <section id="goals">{contentText}</section>
    </div>
  );
};

export default App;
