import React, { useState } from "react";
import GoalInput from "../goalInput/GoalInput";
import "./App.css";

const App = () => {
  const [enteredGoalValue, setEnteredGoalValue] = useState([
    {
      text: "Enter a new goal",
      id: "g1",
    },

    { text: "Fun with this React project", id: "g2" },
  ]);

  let contentText;
  if(enteredGoalValue.length === 0) {
      <p >No Goals found! Maybe add one</p>
  }
  return (
    <div>
      <section id="forms">
        <GoalInput />
      </section>

      <section id='goals'></section>
    </div>
  );
};

export default App;
